filefstab="/etc/fstab"; while IFS=  read -r line_par; do  res_str="$(echo $line_par |  sudo cut -d : -f1)s";  dev_par="$(echo $line_par |  sudo grep -Po ".dev/sr[0-9]")"; dev_par=$(echo "$dev_par" | sudo sed 's/\//\\\//g'); dev_mnt="$(echo $line_par | sudo awk '{print $2}'  )"; dev_mnt=$(echo "$dev_mnt" | sudo sed 's/\//\\\//g'); if [ $dev_par != "" ]; then  sudo sed -i "$res_str/.*/$dev_par        $dev_mnt   udf,iso9660 nouser,noauto     0       0/" $filefstab; fi;  done <<< $(sudo cat $filefstab | sudo grep -n  "^.\dev/sr[0-9].*");
